/* ANSWER QUERY */

SELECT	d.position AS d_position, d.trackname AS d_trackname, d.artist AS d_artist,
		d.url AS d_url, d.ranking_date AS d_stream_date,
		w.position AS w_position, w.trackname AS w_trackname, w.artist AS w_artist,
        w.url AS w_url, w.stream_date AS w_stream_date
FROM	spotify_daily_rankings_2017_us d
		LEFT JOIN spotify_worldwide_daily_song_ranking w
		ON d.url = w.url
WHERE d.position = 1 AND w.position = 1 AND d.ranking_date = w.stream_date
ORDER BY d.trackname;